-- 店長用評価テンプレート作成SQL(44項目)
-- このSQLを全選択してコピー&ペーストで一度に実行できます

WITH new_template AS (
  INSERT INTO evaluation_templates (name, description, created_at, updated_at)
  VALUES ('店長用評価テンプレート(44項目)', '店長・管理職向けの詳細評価基準', NOW(), NOW())
  RETURNING id
)
INSERT INTO evaluation_items (template_id, name, description, weight, category, order_index, grade_scores, grade_criteria, created_at, updated_at)
SELECT
  new_template.id,
  items.name,
  items.description,
  items.weight::integer,
  items.category,
  items.order_index::integer,
  items.grade_scores::jsonb,
  items.grade_criteria::jsonb,
  NOW(),
  NOW()
FROM new_template, (VALUES
  -- Ⅰ.業績評価 (3項目)
  ('店舗売上（達成した月の数）', '店舗売上（達成した月の数）(A.6か月/B.5か月/C.4か月/D.3か月/E.2か月以下)', 1, '業績評価', 1, '{"A": 4, "B": 3.2, "C": 2.4, "D": 1.6, "E": 0.8}', '{"A": "6か月達成", "B": "5か月達成", "C": "4か月達成", "D": "3か月達成", "E": "2か月以下"}'),
  ('店舗の3か月の新規再来率', '店舗の3か月の新規再来率(A.50%/B.43%/C.30%/D.20%/E.10%)', 1, '業績評価', 2, '{"A": 3, "B": 2.4, "C": 1.8, "D": 1.2, "E": 0.6}', '{"A": "50%以上", "B": "43%以上", "C": "30%以上", "D": "20%以上", "E": "10%以上"}'),
  ('店舗入客数（1人のスタッフ平均）', '店舗入客数（1人のスタッフ平均）(A.120人/B.110人/C.100人/D.90人/E.80人)', 1, '業績評価', 3, '{"A": 3, "B": 2.4, "C": 1.8, "D": 1.2, "E": 0.6}', '{"A": "120人以上", "B": "110人以上", "C": "100人以上", "D": "90人以上", "E": "80人以上"}'),

  -- Ⅱ.職務評価 (13項目)
  ('店舗の目標を把握し達成に向けて計画', '店舗の目標を把握し、達成に向けて計画を立て取り組むことができたか', 1, '職務評価', 4, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "目標を数値化し週次・月次計画書を作成。週次で進捗確認、達成率100%以上", "B": "月次計画書を作成し定期的に進捗確認、達成率80-99%", "C": "目標を把握し基本的な計画を立案、行動、達成率60-79%", "D": "目標は把握しているが計画が不明確、達成率40-59%", "E": "目標の把握が不十分、達成率40%未満"}'),
  ('店舗の目標達成の為の行動', '店舗の目標達成の為に行動をおこしたか', 1, '職務評価', 5, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "スキル習得・向上のための練習に加え、後輩指導も実施、セミナー受講、他店ヘルプにて学んだ情報を共有、店舗への貢献も多くみられる", "B": "スキル習得・向上のための練習を率先して実施、自身と店舗の問題点を把握し改善に努める", "C": "スキル習得・向上のための練習を実施、自身の問題点を把握し改善に努める", "D": "技術向上や自身の成長に繋がる取り組みに対して消極的", "E": "現状維持のみ、成長への行動なし"}'),
  ('店舗の予約管理', '店舗の予約管理は適切であったか', 1, '職務評価', 6, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "ダブルブッキング0、施術時間を正確に把握し効率的な予約枠設定", "B": "ミス半年に1件以内、予約管理に問題なし", "C": "ミス半年に2-3件、基本的な予約管理は適切", "D": "ミス半年に4-5件、予約管理に改善必要", "E": "ミス半年に6件以上、予約管理に重大な問題"}'),
  ('指示業務の理解と取り組み', '上司から指示を出された業務に対し、自分自身で理解し取り組めたか', 1, '職務評価', 7, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "一度の説明で完全理解、期待以上の成果、改善提案も実施", "B": "指示を理解し確実に遂行", "C": "基本的に理解し遂行、時々確認必要", "D": "理解不足で再説明が頻繁に必要", "E": "指示を理解できず業務遂行に支障"}'),
  ('仕事の優先順位理解', '仕事の優先順位を理解し行動できているか', 1, '職務評価', 8, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "お客様の予約時間・施術内容から完璧に優先順位判断、準備も先回り", "B": "優先順位を正しく判断し効率的に処理", "C": "基本的な優先順位は理解、通常業務は問題なし", "D": "優先順位の判断ミスが月2-3回、提出期限を稀に過ぎる", "E": "優先順位が判断できず、お客様をよく待たせる、提出期限を守らない"}'),
  ('全スタッフのマニュアル遵守', '全スタッフがマニュアルにそった行動・接客ができていたか', 1, '職務評価', 9, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "カウンセリングからアフターケアまで完璧、お客様に合わせた応用も可能", "B": "施術手順・衛生管理等95%以上遵守", "C": "基本的な施術手順は遵守、重要項目は問題なし", "D": "マニュアル遵守70-89%、指導が必要", "E": "基本的な施術手順も守れていない"}'),
  ('スピーディーでミス無い仕事', '仕事をスピーディーにミス無くこなせているか', 1, '職務評価', 10, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "予定時間内で丁寧な施術、お直し・クレーム0件", "B": "時間内施術、お直し・クレーム半年1件以内", "C": "時間5分超過以内、お直し・クレーム半年2-3件", "D": "時間超過が多い、お直し・クレーム半年4-5件", "E": "常に時間超過、お直し・クレーム半年6件以上"}'),
  ('店舗の整理整頓', '店舗の整理整頓ができているか', 1, '職務評価', 11, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "ワゴン・施術スペース常に完璧、在庫管理も的確、他スタッフの手本", "B": "個人スペース・共有スペース共に整理整頓良好", "C": "基本的に整理整頓、月1回程度指摘", "D": "器具の片付け不足で週1回程度指摘", "E": "常に乱雑、衛生面でも問題"}'),
  ('全スタッフの顧客への寄り添い', '全スタッフがお客様に対して寄り添った行動をとれていたか', 1, '職務評価', 12, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "お客様の要望を汲み取り提案、リピート率90%以上、指名50名以上", "B": "丁寧なカウンセリングと対応、リピート率70-89%、指名40名以上", "C": "標準的な接客、リピート率50-69%、指名20名以上", "D": "接客に改善余地、リピート率30-49%、指名10名以上", "E": "お客様への配慮不足、リピート率30%未満、指名10名未満"}'),
  ('全スタッフの身だしなみ', '全スタッフが清潔かつ健康的な身だしなみでいたか', 1, '職務評価', 13, '{"A": 0, "B": 0, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "髪・ネイル・制服常に完璧、サロンのイメージアップに貢献", "B": "身だしなみ良好、指摘事項なし", "C": "基本的に適切", "D": "ネイルや髪色等で半年1回程度の軽微な指摘", "E": "サロンイメージを損なう身だしなみ、月1回程度指摘"}'),
  ('全スタッフの言葉遣い', '全スタッフが丁寧な言葉使いができていたか', 1, '職務評価', 14, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "完璧な接客用語、お客様との距離感も絶妙、電話応対も模範的", "B": "丁寧な言葉遣い、敬語の間違いほぼなし", "C": "基本的に丁寧、半年に1回言葉遣いの指摘", "D": "言葉遣いに問題、月1~2回程度指摘", "E": "タメ口等不適切な言葉遣いが頻繁"}'),
  ('全スタッフの商品説明', '全スタッフが商品・オプションの説明はできているか', 1, '職務評価', 15, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "ホームケア商品・オプションメニューを完璧に説明、購入率80%以上", "B": "主要商品は完璧に説明、購入率70%", "C": "基本的な説明可能、購入率60%", "D": "説明に不備、資料確認が必要、購入率50%", "E": "商品知識不足で説明できない、購入率50%未満"}'),
  ('全スタッフの電話応対', '全スタッフの電話応対は聞きとりやすいスピード・トーンで対応できていたか', 1, '職務評価', 16, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "明るく丁寧な応対、スピードや音量も適切、他スタッフの手本", "B": "明瞭で丁寧な応対、問題なし", "C": "基本的に適切", "D": "時々改善指摘", "E": "声が小さい・早口等で聞き取りにくい"}'),

  -- Ⅲ.行動評価 - 自主性 (5項目)
  ('自発的行動', '指示をされるのを待つだけでなく、指示を仰いだり、出来ることから自ら段取りを考え工夫をいれ自発的行動だったか', 1, '行動評価-自主性', 17, '{"A": 3, "B": 2, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "次の予約を見越した準備、新メニュー提案、業務改善を月2回以上実施", "B": "自発的に行動、改善提案月1回", "C": "基本的に自発的、時々指示を仰ぐ", "D": "指示待ちが多いが、指示後は行動", "E": "常に指示待ち、自発性なし"}'),
  ('意見の表明', '業務の遂行時や会議・店長ミーティングにおいて自分の考えや意見を述べていたか', 1, '行動評価-自主性', 18, '{"A": 3, "B": 2, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "売上・技術・店舗運営のための建設的意見を積極的発信、採用された提案半年に3件以上", "B": "会議で必ず発言、真っ先に挙手、採用提案半年に1-2件", "C": "挙手、求められれば意見を述べる", "D": "発言消極的、促されても躊躇", "E": "ほとんど発言しない"}'),
  ('方針理解と実践', '会社の方針・理念・目標を理解し、達成に取り組んだか', 1, '行動評価-自主性', 19, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "サロンコンセプトを完全体現、売上目標達成に大きく貢献、新人教育でも伝達", "B": "方針を理解し実践、目標達成に貢献", "C": "基本的な理解と実践", "D": "理解不十分、実践が不足", "E": "方針を理解せず、行動が伴わない"}'),
  ('新しい事への積極性', '新しい事への取り組みは積極的であったか', 1, '行動評価-自主性', 20, '{"A": 2, "B": 1.6, "C": 1.2, "D": 0.8, "E": 0.4}', '{"A": "最新トレンドをいち早く習得、外部セミナー自主参加、店舗にメニュー導入提案、自主レッスンも率先して行う", "B": "新技術に積極的挑戦、自主レッスンも計画的でスムーズ", "C": "指示された技術は習得、自主レッスンも比較的スムーズ", "D": "新技術への抵抗感、自主レッスンは促されて行う", "E": "新技術を避ける、自主レッスンは行わない"}'),
